package training;

public class P05_ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short numOne = 10;
		short numTwo = 6;
		
		System.out.println(numOne + numTwo);
		System.out.println(numOne - numTwo);
		System.out.println(numOne * numTwo);
		System.out.println(numOne / numTwo);
		
		System.out.println(numOne % numTwo);  //called modulo - will return ONLY the remainder after dividing
		System.out.println(numOne + numTwo);

	}

}
