package training;

public class P06_RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short numOne = 10;
		short numTwo = 20;
		
		//>,<,<=,>=,==,!=
		
		// = assignment operator
		// == comparison operator
		
		System.out.println(numOne>numTwo);  //will return a boolean value
		System.out.println(numOne<numTwo);
		System.out.println(numOne>=numTwo);
		System.out.println(numOne<=numTwo);
		System.out.println(numOne==numTwo);
		System.out.println(numOne!=numTwo);
	}

}
