package training;

public class P07_LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numOne= 100;
		int numTwo = 20;
		int numThree = 30;
		
		// && -> AND , || -> OR , ! -> NOT
		
		System.out.println(numOne>numTwo && numOne>numThree);
		
		System.out.println(numOne>numTwo || numOne>numThree);
	}

}
