package training;

public class P08_TernaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numOne= 10;
		int numTwo = 5;
		
		int output = (numOne<numTwo) ? numOne : numTwo;  //IF CONDITION IS TRUE, return the first data... else return the second data
		
		System.out.println(output);
		

	}

}
