package training;

public class p18_DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		
		//for a minimum of one output printed - use a do while
		
		do {
			System.out.println("Good Morning");
			i++;
		} //CODINGITION IS ONLY HAPPENING DURING THE WHILE PORTION
		while (i<5); 

	}

}
